import React, { useState, useEffect } from 'react';
import '../styles/Chatbot.css';

const ElicitationTool = () => {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Welcome to the elicitation tool! Let's begin." }
  ]);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [metaModel, setMetaModel] = useState(null);
  const [responses, setResponses] = useState({});
  const [input, setInput] = useState('');
  const [questionIndex, setQuestionIndex] = useState(0);

  const generateQuestion = (attribute) => {
    if (attribute.type === 'string') {
      return `What is the ${attribute.label.toLowerCase()}?`;
    }
    if (attribute.type === 'array' && attribute.options) {
      return `Please choose an option for ${attribute.label.toLowerCase()}: ${attribute.options.join(', ')}.`;
    }
    if (attribute.type === 'array' && !attribute.options) {
      return `List the ${attribute.label.toLowerCase()} (e.g., ${attribute.example || 'example data'}).`;
    }
    if (attribute.type === 'object' && attribute.attributes) {
      return `Provide details for ${attribute.label.toLowerCase()}.`;
    }
    return `Can you provide information about ${attribute.label.toLowerCase()}?`;
  };

  useEffect(() => {
    const fetchMetaModel = async () => {
      try {
        const response = await fetch('/api/fetch-meta-model');
        const text = await response.text();
        console.log("Response text: ", text);
        const data = JSON.parse(text);
        console.log(data);
        if (data && data.reportMetaModel && data.reportMetaModel.attributes) {
          setMetaModel(data.reportMetaModel.attributes);
          setCurrentQuestion(data.reportMetaModel.attributes[0]);
          setMessages(prev => [
            ...prev,
            { sender: "bot", text: generateQuestion(data.reportMetaModel.attributes[0]) }
          ]);
        } else {
          setMessages(prev => [
            ...prev,
            { sender: "bot", text: "Failed to fetch the meta-model. Please try again later." }
          ]);
        } 
      } catch (err) {
        console.error("Error fetching data: ",err);
        setMessages(prev => [
          ...prev,
          { sender: "bot", text: "Failed to fetch the meta-model. Please try again later." }
        ]);
      }
    };

    fetchMetaModel();
  }, []);

  const handleSendMessage = () => {
    if (input.trim()) {
      const updatedResponses = { ...responses, [currentQuestion.name]: input };
      setResponses(updatedResponses);

      setMessages(prev => [...prev, { sender: "user", text: input }]);

      setInput('');

      if (questionIndex + 1 < metaModel.length) {
        const nextQuestion = metaModel[questionIndex + 1];
        setTimeout(() => {
          setMessages(prev => [
            ...prev,
            { sender: "bot", text: `${nextQuestion.label}: ${nextQuestion.description}` }
          ]);
          setCurrentQuestion(nextQuestion);
          setQuestionIndex(questionIndex + 1);
        }, 1000);
      } else {
        setTimeout(() => {
          setMessages(prev => [
            ...prev,
            { sender: "bot", text: "Thanks for your responses! Submitting them now." }
          ]);
          submitResponses(updatedResponses);
        }, 1000);
      }
    }
  };

  const submitResponses = async (responses) => {
    try {
      const response = await fetch('/api/submit-responses', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(responses),
      });
      const data = await response.json();
      setMessages(prev => [
        ...prev,
        { sender: "bot", text: "Your responses have been saved. Thank you!" }
      ]);
    } catch (err) {
      setMessages(prev => [
        ...prev,
        { sender: "bot", text: "Failed to save responses. Please try again later." }
      ]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((message, index) => (
          <div key={index} className={`chatbot-message ${message.sender}`}>
            {message.text}
          </div>
        ))}
      </div>
      {currentQuestion && (
        <div className="chatbot-input">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyPress}
            placeholder={`Type your message...`}
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      )}
    </div>
  );
};

export default ElicitationTool;
