import React, { useState, useEffect } from 'react';
import '../styles/Chatbot.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [metaModel, setMetaModel] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userResponses, setUserResponses] = useState({});
  const [hasWelcomeMessageShown, setHasWelcomeMessageShown] = useState(false);  // Track if welcome message has been shown

  // Fetch the meta-model on first load
  useEffect(() => {
    const fetchMetaModel = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/meta-model', {
          method: 'GET',
        });

        if (response.ok) {
          const data = await response.json();
          setMetaModel(data);  // Store fetched data

          if (!hasWelcomeMessageShown) {
            setMessages(prevMessages => [
              ...prevMessages,
              { sender: 'bot', text: 'Welcome to the chat!' }  // Show welcome message once
            ]);
            setHasWelcomeMessageShown(true);  // Mark that welcome message is shown
          }

          setMessages(prevMessages => [
            ...prevMessages,
            { sender: 'bot', text: `Please specify the ${data.data.reportMetaModel.attributes[0].label}.` }
          ]);
        } else {
          throw new Error('Failed to fetch meta-model');
        }
      } catch (error) {
        console.error('Error fetching meta-model:', error);
        setMessages(prevMessages => [
          ...prevMessages,
          { sender: 'bot', text: `Error: ${error.message}` }
        ]);
      }
    };

    if (!metaModel) {
      fetchMetaModel();
    }
  }, [metaModel, hasWelcomeMessageShown]);

  const handleSendMessage = () => {
    if (input.trim()) {
      setMessages(prevMessages => [...prevMessages, { sender: 'user', text: input }]);
      setInput(''); // Clear input after sending

      if (metaModel) {
        const currentAttribute = metaModel.data.reportMetaModel.attributes[currentQuestionIndex];
        setUserResponses(prevResponses => ({
          ...prevResponses,
          [currentAttribute.name]: input.trim()
        }));

        // If there are more questions, ask the next one
        const nextQuestionIndex = currentQuestionIndex + 1;
        if (nextQuestionIndex < metaModel.data.reportMetaModel.attributes.length) {
          const nextAttribute = metaModel.data.reportMetaModel.attributes[nextQuestionIndex];
          setMessages(prevMessages => [
            ...prevMessages,
            { sender: 'bot', text: `Please specify the ${nextAttribute.label}.` }
          ]);
          setCurrentQuestionIndex(nextQuestionIndex); // Move to next question
        } else {
          // All questions asked, show summary
          summarizeResponses();
        }
      }
    }
  };

  const summarizeResponses = () => {
    let summary = 'Here is the summary of your responses:\n';
    metaModel.data.reportMetaModel.attributes.forEach(attribute => {
      const userResponse = userResponses[attribute.name];
      summary += `${attribute.label}: ${userResponse}\n`;
    });
    setMessages(prevMessages => [...prevMessages, { sender: 'bot', text: summary }]);
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((message, index) => (
          <div key={index} className={`chatbot-message ${message.sender}`}>
            {message.text}
          </div>
        ))}
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
