import React, { useState } from 'react';
import '../styles/Chatbot.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hello! How can I help you today?" }
  ]);
  const [input, setInput] = useState('');
  const [metaModel, setMetaModel] = useState(null);

  const fetchMetaModel = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/meta-model', {
        method: 'GET',
      });
      if (response.ok) {
        const data = await response.json();
        console.log('Meta-model data:', data);  // Debugging: Check what the response looks like
        setMetaModel(data);
        setMessages(prev => [...prev, { sender: "bot", text: "Here's the meta-model data!" }]);
        setTimeout(() => {
          setMessages(prev => [...prev, { sender: "bot", text: JSON.stringify(data, null, 2) }]);
        }, 1000); // Delay to format the data properly
      } else {
        throw new Error('Failed to fetch meta-model');
      }
    } catch (error) {
      console.error('Error fetching meta-model:', error);
      setMessages(prev => [...prev, { sender: "bot", text: `Error: ${error.message}` }]);
    }
  };

  const handleSendMessage = () => {
    if (input.trim()) {
      setMessages([...messages, { sender: "user", text: input }]);
      setInput('');
      if (input.toLowerCase().includes('meta-model')) {
        fetchMetaModel();
      } else {
        setTimeout(() => {
          setMessages(prev => [...prev, { sender: "bot", text: "Thanks for the input! What else?" }]);
        }, 1000);
      }
    }
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((message, index) => (
          <div key={index} className={`chatbot-message ${message.sender}`}>
            {message.text}
          </div>
        ))}
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
