import React, { useState, useEffect } from 'react';
import '../styles/Chatbot.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hello! How can I help you today?" }
  ]);
  const [input, setInput] = useState('');
  const [metaModel, setMetaModel] = useState(null);
  const [userResponses, setUserResponses] = useState({});
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  // Fetch the meta-model from the backend
  const fetchMetaModel = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/meta-model', {
        method: 'GET',
      });

      if (response.ok) {
        const data = await response.json();
        setMetaModel(data);
        askNextQuestion(data);  // Start asking questions after data is fetched
      } else {
        throw new Error('Failed to fetch meta-model');
      }
    } catch (error) {
      console.error('Error fetching meta-model:', error);
      setMessages(prev => [...prev, { sender: "bot", text: `Error: ${error.message}` }]);
    }
  };

  useEffect(() => {
    fetchMetaModel();  // Start by fetching the meta-model
  }, []);

  // Function to handle sending user messages and responses
  const handleSendMessage = () => {
    if (input.trim()) {
      if (!metaModel) {
        // If metaModel isn't fetched yet, return early
        return;
      }

      const currentAttribute = metaModel?.reportMetaModel?.attributes[currentQuestionIndex];

      if (!currentAttribute) {
        // If the current attribute is undefined, avoid proceeding further
        return;
      }

      const response = input.trim();

      // Save the response from the user
      setUserResponses(prevResponses => ({
        ...prevResponses,
        [currentAttribute.name]: response
      }));

      setMessages([...messages, { sender: "user", text: input }]);
      setInput('');

      // Move to the next question or summarize responses
      if (currentQuestionIndex < metaModel?.reportMetaModel?.attributes.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
      } else {
        summarizeResponses();
      }
    }
  };

  // Summarize the responses after all questions have been answered
  const summarizeResponses = () => {
    let summary = 'Here is the summary of your responses:\n';
    metaModel?.reportMetaModel?.attributes.forEach(attribute => {
      const userResponse = userResponses[attribute.name];
      summary += `${attribute.label}: ${userResponse}\n`;
    });
    setMessages(prev => [...prev, { sender: "bot", text: summary }]);
  };

  // Generate the question text based on the attribute
  const generateQuestion = (attribute) => {
    let question = `Please specify the ${attribute.label}. `;
    if (attribute.description) {
      question += `${attribute.description} `;
    }
    if (attribute.options && attribute.options.length > 0) {
      question += `You can choose from: ${attribute.options.join(', ')}. `;
    }
    question += `For example: ${attribute.example}.`;
    return question;
  };

  // Handle complex field types such as arrays or objects
  const handleComplexField = (attribute) => {
    if (attribute.type === 'array') {
      return (
        <div className="chatbot-message bot">
          {`Please specify the ${attribute.label} (e.g., ${attribute.example}). You can provide multiple values separated by commas.`}
        </div>
      );
    }

    if (attribute.type === 'object' && attribute.attributes) {
      return attribute.attributes.map((subAttribute) => {
        return (
          <div key={subAttribute.name} className="chatbot-message bot">
            {generateQuestion(subAttribute)}
          </div>
        );
      });
    }

    return (
      <div className="chatbot-message bot">
        {generateQuestion(attribute)}
      </div>
    );
  };

  // Ask the next question based on the current question index
  const askNextQuestion = (data) => {
    const currentAttribute = data?.reportMetaModel?.attributes[currentQuestionIndex];
    if (currentAttribute) {
      const questionText = handleComplexField(currentAttribute);
      setMessages(prev => [...prev, { sender: "bot", text: questionText }]);
    }
  };

  // Display the current question based on the attribute type
  const currentAttribute = metaModel?.reportMetaModel?.attributes[currentQuestionIndex];
  const questionText = currentAttribute ? handleComplexField(currentAttribute) : '';

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((message, index) => (
          <div key={index} className={`chatbot-message ${message.sender}`}>
            {message.text}
          </div>
        ))}
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
