import React, { useState, useEffect, useRef } from 'react';
import '../styles/Chatbot.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [metaModel, setMetaModel] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userResponses, setUserResponses] = useState({});
  const [isDefaultQuestionAsked, setIsDefaultQuestionAsked] = useState(false);
  const messagesEndRef = useRef(null);

  const defaultQuestion = "Before we finish, is there anything else you'd like to share or clarify?";

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Fetch meta-model on mount
  useEffect(() => {
    const fetchMetaModel = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/meta-model');
        if (!response.ok) throw new Error('Failed to fetch meta-model');

        const data = await response.json();
        setMetaModel(data);

        setMessages([
          { sender: 'bot', text: 'Welcome to the chat!' },
          { sender: 'bot', text: `Let’s get started. Could you please specify the ${data.data.reportMetaModel.attributes[0].label}?` },
        ]);
      } catch (error) {
        console.error('Error fetching meta-model:', error);
        setMessages([{ sender: 'bot', text: `Error: ${error.message}` }]);
      }
    };

    fetchMetaModel();
  }, []);

  const handleSendMessage = async () => {
    if (input.trim()) {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'user', text: input.trim() },
      ]);

      if (isDefaultQuestionAsked) {
        handleDefaultQuestion(input.trim());
      } else {
        handleMetaModelQuestions();
      }

      setInput('');
    }
  };

  const handleMetaModelQuestions = () => {
    const questionQueue = [];
    const responses = { ...userResponses };

    const enqueueQuestions = (attribute, path = []) => {
      if (attribute.type === 'array' && attribute.itemType) {
        const arrayPath = [...path, attribute.name];
        const arrayResponses = arrayPath.reduce((acc, key) => acc[key] || [], responses);

        // Add existing array items to queue
        arrayResponses.forEach((_, index) => {
          questionQueue.push({
            path: [...arrayPath, index],
            attribute: attribute.itemType,
          });
        });

        // Add a prompt for a new array item
        questionQueue.push({
          path: arrayPath,
          attribute,
          newArrayItem: true,
        });
      } else if (attribute.type === 'object' && attribute.attributes) {
        // Process nested attributes
        attribute.attributes.forEach((nestedAttr) => {
          enqueueQuestions(nestedAttr, [...path, attribute.name]);
        });
      } else {
        // Primitive attributes
        questionQueue.push({
          path: [...path, attribute.name],
          attribute,
        });
      }
    };

    metaModel.data.reportMetaModel.attributes.forEach((attr) => enqueueQuestions(attr));

    const processNextQuestion = (userInput = null) => {
      if (userInput !== null) {
        // Save user input
        const { path, newArrayItem } = questionQueue[0];
        let currentLevel = responses;

        path.slice(0, -1).forEach((key) => {
          if (!currentLevel[key]) currentLevel[key] = isNaN(key) ? {} : [];
          currentLevel = currentLevel[key];
        });

        const key = path[path.length - 1];
        if (newArrayItem) {
          currentLevel[key] = currentLevel[key] || [];
          currentLevel[key].push({});
        } else {
          currentLevel[key] = userInput;
        }

        setUserResponses(responses);
      }

      questionQueue.shift(); // Remove the processed question

      if (questionQueue.length === 0) {
        summarizeResponses();
        return;
      }

      const { path, attribute, newArrayItem } = questionQueue[0];
      const questionText = newArrayItem
        ? `Would you like to add a new item to ${attribute.label}? (yes/no)`
        : `Could you please specify the ${attribute.label}?`;

      setMessages((prev) => [...prev, { sender: 'bot', text: questionText }]);
    };

    const handleUserInput = (userInput) => {
      if (questionQueue[0]?.newArrayItem) {
        if (userInput.toLowerCase() === 'yes') {
          const { path, attribute } = questionQueue[0];
          questionQueue.unshift({
            path: [...path, attribute.itemType.name],
            attribute: attribute.itemType,
            newArrayItem: false,
          });
        } else if (userInput.toLowerCase() === 'no') {
          questionQueue.shift(); // Skip new item addition
        }
      }
      processNextQuestion(userInput);
    };

    processNextQuestion();
  };

  const summarizeResponses = async () => {
    let summary = 'Here is a quick summary the responses you gave:\n';

    metaModel.data.reportMetaModel.attributes.forEach((attr) => {
      const userResponse = userResponses[attr.name];
      if (attr.type === 'object' && attr.attributes) {
        summary += `${attr.label}:, `;
        attr.attributes.forEach((nestedAttr) => {
          const nestedResponse = userResponse?.[nestedAttr.name] || 'N/A';
          summary += `   - ${nestedAttr.label}: ${nestedResponse}, `;
        });
      } else {
        summary += `${attr.label}: ${userResponse || 'N/A'}, `;
      }
    });

    setMessages((prevMessages) => [
      ...prevMessages,
      { sender: 'bot', text: "Here's what we have so far:" },
      { sender: 'bot', text: summary },
      { sender: 'bot', text: defaultQuestion },
    ]);

    setIsDefaultQuestionAsked(true);

    try {
      const response = await fetch('http://localhost:5001/api/save-user-responses', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userResponses, userId: 'guest' }),
      });

      if (!response.ok) {
        console.error('Error saving responses:', await response.json());
      }
    } catch (error) {
      console.error('Error saving responses:', error);
    }
  };

  const handleDefaultQuestion = (response) => {
    const lowercasedResponse = response.toLowerCase();
    if (lowercasedResponse === 'no' || lowercasedResponse === 'leave') {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'bot', text: 'Thank you for your time! If you need further requirements, feel free to reach out again. Have a great day!' },
      ]);
      setIsDefaultQuestionAsked(false);
    } else {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'bot', text: 'Got it. Please share any additional details or clarifications you have.' },
      ]);
    }
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`chatbot-message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef}></div>
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
