import React, { useState, useEffect, useRef } from 'react';
import '../styles/Chatbot.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [metaModel, setMetaModel] = useState(null);
  const [userResponses, setUserResponses] = useState({});
  const [isDefaultQuestionAsked, setIsDefaultQuestionAsked] = useState(false);
  const messagesEndRef = useRef(null);

  const defaultQuestion = "Before we finish, is there anything else you'd like to share or clarify?";

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  useEffect(() => {
    const fetchMetaModel = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/meta-model');
        if (!response.ok) throw new Error('Failed to fetch meta-model');

        const data = await response.json();
        setMetaModel(data);

        setMessages([
          { sender: 'bot', text: 'Welcome to the chat!' },
          { sender: 'bot', text: `Let’s get started. Could you please specify the ${data.data.reportMetaModel.attributes[0].label}?` },
        ]);
      } catch (error) {
        console.error('Error fetching meta-model:', error);
        setMessages([{ sender: 'bot', text: `Error: ${error.message}` }]);
      }
    };

    fetchMetaModel();
  }, []);

  const handleSendMessage = () => {
    if (input.trim()) {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'user', text: input.trim() },
      ]);

      if (isDefaultQuestionAsked) {
        handleDefaultQuestion(input.trim());
      } else {
        processNextQuestion(input.trim());
      }

      setInput('');
    }
  };

  const enqueueQuestions = (metaAttributes, responses = userResponses, path = []) => {
    const questionQueue = [];

    const processAttributes = (attributes, parentPath) => {
      attributes.forEach((attribute) => {
        const fullPath = [...parentPath, attribute.name];
        if (attribute.type === 'object' && attribute.attributes) {
          processAttributes(attribute.attributes, fullPath);
        } else if (attribute.type === 'array' && attribute.itemType) {
          const arrayData = fullPath.reduce((acc, key) => acc[key] || [], responses);
          arrayData.forEach((_, index) => {
            questionQueue.push({ path: [...fullPath, index], attribute: attribute.itemType });
          });
          questionQueue.push({ path: fullPath, attribute, newArrayItem: true });
        } else {
          questionQueue.push({ path: fullPath, attribute });
        }
      });
    };

    processAttributes(metaAttributes, path);
    return questionQueue;
  };

  const processNextQuestion = (userInput = null) => {
    if (!metaModel) return;

    const metaAttributes = metaModel.data.reportMetaModel.attributes;
    const questionQueue = enqueueQuestions(metaAttributes);

    const handleQueue = (queue, input = null) => {
      if (queue.length === 0) {
        summarizeResponses();
        return;
      }

      const { path, attribute, newArrayItem } = queue.shift();

      if (input !== null) {
        setUserResponses((prevResponses) => {
          const updatedResponses = { ...prevResponses };
          let currentLevel = updatedResponses;
          path.slice(0, -1).forEach((key) => {
            if (!currentLevel[key]) currentLevel[key] = isNaN(key) ? {} : [];
            currentLevel = currentLevel[key];
          });

          const key = path[path.length - 1];
          if (newArrayItem) {
            currentLevel[key] = currentLevel[key] || [];
            currentLevel[key].push({});
          } else {
            currentLevel[key] = input;
          }

          return updatedResponses;
        });
      }

      if (queue.length > 0) {
        const next = queue[0];
        const questionText = next.newArrayItem
          ? `Would you like to add a new item to ${next.attribute.label}? (yes/no)`
          : `Could you please specify the ${next.attribute.label}?`;
        setMessages((prev) => [...prev, { sender: 'bot', text: questionText }]);
      } else {
        summarizeResponses();
      }
    };

    handleQueue(questionQueue, userInput);
  };

  const summarizeResponses = async () => {
    const summaryParts = metaModel.data.reportMetaModel.attributes.map((attr) => {
      const response = userResponses[attr.name];
      if (attr.type === 'object' && attr.attributes) {
        const nestedParts = attr.attributes.map(
          (nestedAttr) => `${nestedAttr.label}: ${response?.[nestedAttr.name] || 'N/A'}`
        );
        return `${attr.label}:\n  ${nestedParts.join('\n  ')}`;
      } else {
        return `${attr.label}: ${response || 'N/A'}`;
      }
    });

    const summary = summaryParts.join('\n');

    setMessages((prevMessages) => [
      ...prevMessages,
      { sender: 'bot', text: "Here's what we have so far:" },
      { sender: 'bot', text: summary },
      { sender: 'bot', text: defaultQuestion },
    ]);

    setIsDefaultQuestionAsked(true);

    try {
      const response = await fetch('http://localhost:5001/api/save-user-responses', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userResponses, userId: 'guest' }),
      });

      if (!response.ok) {
        console.error('Error saving responses:', await response.json());
      }
    } catch (error) {
      console.error('Error saving responses:', error);
    }
  };

  const handleDefaultQuestion = (response) => {
    const lowercasedResponse = response.toLowerCase();
    if (lowercasedResponse === 'no' || lowercasedResponse === 'leave') {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'bot', text: 'Thank you for your time! If you need further requirements, feel free to reach out again. Have a great day!' },
      ]);
      setIsDefaultQuestionAsked(false);
    } else {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'bot', text: 'Got it. Please share any additional details or clarifications you have.' },
      ]);
    }
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`chatbot-message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef}></div>
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
