import React, { useState, useEffect } from 'react';
import '../styles/Chatbot.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hello! How can I help you today?" }
  ]);
  const [input, setInput] = useState('');
  const [metaModel, setMetaModel] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userResponses, setUserResponses] = useState({});

  useEffect(() => {
    // Fetch meta-model once on component mount
    const fetchMetaModel = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/meta-model', {
          method: 'GET',
        });

        if (response.ok) {
          const data = await response.json();
          setMetaModel(data);  // Store meta-model data
          setMessages(prevMessages => [
            ...prevMessages,
            { sender: 'bot', text: data.msg || 'Welcome to the chat!' }
          ]);
        } else {
          throw new Error('Failed to fetch meta-model');
        }
      } catch (error) {
        console.error('Error fetching meta-model:', error);
        setMessages(prevMessages => [
          ...prevMessages,
          { sender: 'bot', text: `Error: ${error.message}` }
        ]);
      }
    };

    fetchMetaModel();
  }, []); // Empty dependency array ensures it only runs once on mount

  useEffect(() => {
    // After meta-model is loaded, ask the first question
    if (metaModel && currentQuestionIndex === 0) {
      const currentAttribute = metaModel.data.reportMetaModel.attributes[currentQuestionIndex];
      const question = `Please specify the ${currentAttribute.label}.`;

      setMessages(prevMessages => [
        ...prevMessages,
        { sender: 'bot', text: question }
      ]);
      setCurrentQuestionIndex(prevIndex => prevIndex + 1); // Move to the next question
    }
  }, [metaModel, currentQuestionIndex]); // Trigger when meta-model or currentQuestionIndex changes

  const handleSendMessage = () => {
    if (input.trim()) {
      setMessages(prevMessages => [...prevMessages, { sender: "user", text: input }]);
      setInput(''); // Clear input after sending

      if (metaModel && currentQuestionIndex < metaModel.data.reportMetaModel.attributes.length) {
        // Save the user response to the current question
        const currentAttribute = metaModel.data.reportMetaModel.attributes[currentQuestionIndex - 1]; // Use previous question index
        setUserResponses(prevResponses => ({
          ...prevResponses,
          [currentAttribute.name]: input.trim()
        }));

        // Ask the next question if there are more questions
        const nextAttribute = metaModel.data.reportMetaModel.attributes[currentQuestionIndex];
        if (nextAttribute) {
          setMessages(prevMessages => [
            ...prevMessages,
            { sender: 'bot', text: `Please specify the ${nextAttribute.label}.` }
          ]);
          setCurrentQuestionIndex(prevIndex => prevIndex + 1); // Move to the next question
        } else {
          // If no more questions, summarize the responses
          summarizeResponses();
        }
      }
    }
  };

  const summarizeResponses = () => {
    let summary = 'Here is the summary of your responses:\n';
    metaModel.data.reportMetaModel.attributes.forEach(attribute => {
      const userResponse = userResponses[attribute.name];
      summary += `${attribute.label}: ${userResponse}\n`;
    });
    setMessages(prevMessages => [...prevMessages, { sender: 'bot', text: summary }]);
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((message, index) => (
          <div key={index} className={`chatbot-message ${message.sender}`}>
            {message.text}
          </div>
        ))}
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
